mapper.select:
SELECT 

FROM vpl_order_import a

WHERE a.id = #{id}

SELECT

FROM vpl_order_import a


	a.del_flag = 0

		AND a.pro_model = #{proModel}


		AND a.order_id = #{orderId}

		SELECT 

		FROM vpl_order_import a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.cus_name LIKE concat('%', #{cusName},'%')


				AND a.order_id =#{orderId}


				AND a.pro_model =#{proModel}



                AND a.order_date >= #{orderDateS}


                AND a.order_date <= #{orderDateE}


                AND a.order_date >= #{orderDateStr}


                AND a.order_date <= #{orderDateEtr}


				AND a.counts-a.has_counts !=0





				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM vpl_order_import a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE vpl_order_import SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
